// Utility function to generate blur data URLs for images
export function generateBlurDataURL(width: number = 10, height: number = 10): string {
  // Create a simple 1x1 pixel image as base64
  const canvas = typeof document !== 'undefined' ? document.createElement('canvas') : null;
  if (!canvas) {
    // Fallback blur data URL for SSR
    return 'data:image/jpeg;base64,';
  }

  canvas.width = width;
  canvas.height = height;
  const ctx = canvas.getContext('2d');
  
  if (!ctx) {
    return 'data:image/jpeg;base64,';
  }

  // Create a simple gradient blur effect
  const gradient = ctx.createLinearGradient(0, 0, width, height);
  gradient.addColorStop(0, '#f0f0f0');
  gradient.addColorStop(0.5, '#e0e0e0');
  gradient.addColorStop(1, '#d0d0d0');
  
  ctx.fillStyle = gradient;
  ctx.fillRect(0, 0, width, height);
  
  return canvas.toDataURL('image/jpeg', 0.1);
}

// Predefined blur data URLs for common use cases
export const BLUR_DATA_URLS = {
  airline: 'data:image/jpeg;base64,',
  hero: 'data:image/jpeg;base64,',
  default: 'data:image/jpeg;base64,'
} as const;
